const mnemonicPhrase = "gentle coffee topic impose couple reunion ask daring boat fresh below retreat";  

let provider;
let web3;

test.before((t) => {
  // init provider and web3
  const godwoken_rpc_url = process.env.WEB3_JSON_RPC;
  const polyjuice_config: PolyjuiceConfig = {
    rollupTypeHash: process.env.ROLLUP_TYPE_HASH,
    ethAccountLockCodeHash: process.env.ETH_ACCOUNT_LOCK_CODE_HASH,
    abiItems: SimpleStorageV2_Abi as AbiItems,
    web3Url: godwoken_rpc_url,
  };

  const polyjuiceHttpProvider = new PolyjuiceHttpProvider(polyjuice_config.web3Url, polyjuice_config);

  provider = new PolyjuiceHDWalletProvider([{
    mnemonic: mnemonicPhrase,
    providerOrUrl: polyjuiceHttpProvider
  }], polyjuice_config);

  web3 = new Web3(provider);
});

test.serial("import class", (t) => {
  t.not(provider, undefined);
});

test.serial("sign tx", async (t) => {
  const txReceipt = await web3.eth.sendTransaction({
    from: process.env.ETH_ADDRESS,
    to: '0x'+'0'.repeat(40),
    value: '0x00',
    data: '0x00',
    gas: '0x5def00',
    gasPrice: '0x00'
  });
  t.not(txReceipt, undefined);
  t.is(txReceipt.transactionHash.slice(0, 2), '0x');
});